import React from 'react';
import tour from '../../assets/tourEiffel.jpg';
import Felix from '../../assets/FélixRecad.jpg';

const Presentation = () => {
    return (
        <div className='flex flex-col lg:flex-row'>
            <div>
                <img
                    src={Felix}
                    alt='photo Felix'
                    className='hidden h-[10rem] md:block rounded-3xl md:mx-auto md:items-center md:mb-10'
                />
            </div>
            <cite className='hidden tmx-autor text-2xl leading-8 md:block lg:flex-1 lg:leading-10 lg:max-w-[700px]'>
                " Tous les hommes utiles doivent être en rapport les uns avec les
                autres, de même que l'entrepreneur se réfère à l'architecte et
                celui-ci au maçon et au charpentier. "
            </cite>
            <div>
                <img
                    src={tour}
                    alt='tour Eiffel'
                    className='mx-auto rounded-3xl md:hidden'
                />
            </div>
        </div>
    );
};

export default Presentation;
