import React from 'react';
import tour from '../../assets/tourEiffel.jpg';
import Felix from '../../assets/FélixRecad.jpg';

const Presentation = () => {
    return (
        <div className='flex flex-col lg:justify-around'>
            <div className='flex w-full lg:flex-row lg:flex-wrap'>
                <div>
                    <img
                        src={Felix}
                        alt='photo Felix'
                        className='hidden h-[10rem] md:block rounded-3xl md:mx-auto md:items-center md:mb-8 lg:mb-0'
                    />
                </div>
                <cite className= 'hidden text-center text-2xl leading-8 md:block lg:leading-10 lg:w-[600px] lg:text-3xl'>
                    " Tous les hommes utiles doivent être en rapport les uns
                    avec les autres, de même que l'entrepreneur se réfère à
                    l'architecte et celui-ci au maçon et au charpentier. "
                </cite>
                <div className='mx-auto'>
                    <img
                        src={tour}
                        alt='tour Eiffel'
                        className='rounded-3xl md:hidden'
                    />
                </div>
            </div>
        </div>
    );
};

export default Presentation;
