import React from 'react';
import video from '../../assets/Presentation2022.mp4';
import ensemble from '../../assets/ensemble.jpg';
import ensembleFront from '../../assets/ensembleFront.jpg';
import ensembleFond from '../../assets/ensembleFond.jpg';

import PropTypes from 'prop-types'

const Curriculum = props => {
  return (
    <div>
      <ul className='text-center'>
        <li className='mb-4'>Depuis 2019 : Adhérent au CAUE et élève aux cours et stages d'architecture</li>
        <li className='mb-6'>Juin 2022 : Présentation de nos travaux devant les parents d'élèves (casque audio conseillé !)</li>
        <video className='lg:max-w-[700px] mx-auto mb-4' controls>
          <source src={video} type="video/mp4"/>
          Votre navigateur ne supporte pas l'affichage de vidéos.
        </video>
        <li className='mb-4'>Mes maquettes réalisées au cours de l'année 2022</li>
        <li className='mb-4'><img src={ensemble} alt="ensemble-maquettes" className='lg:max-w-[800px] mx-auto'/></li>
        <li className='mb-4'><img src={ensembleFront} alt="ensemble-maquettes" className='lg:max-w-[800px] mx-auto'/></li>
        <li className='mb-4'><img src={ensembleFond} alt="ensemble-maquettes" className='lg:max-w-[800px] mx-auto'/></li>
      </ul>
    </div>
  )
}

Curriculum.propTypes = {}

export default Curriculum;
